import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FeedbackDashboard = () => {
  const [feedbacks, setFeedbacks] = useState([]);
  const [category, setCategory] = useState('');
  const [sortBy, setSortBy] = useState('timestamp');

  useEffect(() => {
    const fetchData = async () => {
      const res = await axios.get(`http://localhost:5000/feedback?category=${category}&sortBy=${sortBy}`);
      setFeedbacks(res.data);
    };
    fetchData();
  }, [category, sortBy]);

  return (
    <div
      style={{
        minHeight: '60vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'white',
        borderRadius: '20px',
        boxShadow: '0 8px 24px rgba(0,0,0,0.08)',
        padding: '32px 24px',
        margin: '40px auto',
        maxWidth: '700px'
      }}
    >
      <h2 style={{ color: '#28a745', marginBottom: '24px', letterSpacing: '1px' }}>Feedback Dashboard</h2>
      <div style={{ display: 'flex', gap: '16px', marginBottom: '24px' }}>
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          style={{
            padding: '10px 16px',
            borderRadius: '8px',
            border: '1px solid #ccc',
            fontSize: '16px',
            outline: 'none',
            background: '#f7f7f7'
          }}
        >
          <option value="">All</option>
          <option value="suggestion">Suggestion</option>
          <option value="bug">Bug</option>
          <option value="feature">Feature</option>
        </select>
        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          style={{
            padding: '10px 16px',
            borderRadius: '8px',
            border: '1px solid #ccc',
            fontSize: '16px',
            outline: 'none',
            background: '#f7f7f7'
          }}
        >
          <option value="timestamp">Latest</option>
          <option value="name">Name</option>
        </select>
      </div>
      <ul
        style={{
          width: '100%',
          maxHeight: '20vh', // <-- Updated here
          overflowY: 'auto',
          listStyle: 'none',
          padding: 0,
          margin: 0,
          display: 'flex',
          flexDirection: 'column',
          gap: '18px'
        }}
      >
        {feedbacks.length === 0 && (
          <li style={{ color: '#888', textAlign: 'center', fontStyle: 'italic' }}>No feedback found.</li>
        )}
        {feedbacks.map(f => (
          <li
            key={f._id}
            style={{
              background: '#f9f9f9',
              borderRadius: '12px',
              boxShadow: '0 2px 8px rgba(40,167,69,0.07)',
              padding: '18px 20px',
              transition: 'box-shadow 0.2s',
              borderLeft: '4px solid #28a745'
            }}
          >
            <div style={{ marginBottom: '4px', fontWeight: 600, fontSize: '17px', color: '#222' }}>
              {f.name} <span style={{ color: '#888', fontWeight: 400 }}>({f.email})</span>
              <span
                style={{
                  background: '#eafbee',
                  color: '#28a745',
                  borderRadius: '6px',
                  padding: '2px 10px',
                  fontSize: '14px',
                  marginLeft: '10px',
                  textTransform: 'capitalize'
                }}
              >
                {f.category}
              </span>
            </div>
            <div style={{ marginBottom: '10px', color: '#444', fontSize: '16px' }}>
              {f.text}
            </div>
            <div style={{ fontSize: '13px', color: '#aaa' }}>
              {new Date(f.timestamp).toLocaleString()}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FeedbackDashboard;
