import React, { useState } from 'react';
import axios from 'axios';

const FeedbackForm = () => {
  const [formData, setFormData] = useState({ name: '', email: '', text: '', category: 'suggestion' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/feedback', formData);
    alert('Feedback submitted!');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input placeholder="Name" required value={formData.name} onChange={(e) => setFormData({...formData, name: e.target.value})} />
      <input type="email" placeholder="Email" required value={formData.email} onChange={(e) => setFormData({...formData, email: e.target.value})} />
      <textarea placeholder="Feedback" required value={formData.text} onChange={(e) => setFormData({...formData, text: e.target.value})} />
      <select value={formData.category} onChange={(e) => setFormData({...formData, category: e.target.value})}>
        <option value="suggestion">Suggestion</option>
        <option value="bug">Bug</option>
        <option value="feature">Feature Request</option>
      </select>
      <button type="submit">Submit</button>
    </form>
  );
};

export default FeedbackForm;
