import React, { useState } from 'react';
import axios from 'axios';

const FeedbackForm = () => {
  const [formData, setFormData] = useState({ name: '', email: '', text: '', category: 'suggestion' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/feedback', formData);
    alert('Feedback submitted!');
    setFormData({ name: '', email: '', text: '', category: 'suggestion' });
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        display: 'flex',
        flexDirection: 'column',
        gap: '12px',
        width: '400px',
        height: '50vh',  // Slightly more height to accommodate the larger button
        margin: '5vh auto', // Top margin to center nicely
        padding: '20px',
        border: '1px solid #ccc',
        borderRadius: '8px',
        backgroundColor: '#f9f9f9',
        boxSizing: 'border-box',
        justifyContent: 'space-between',
      }}
    >
      <h2 style={{ textAlign: 'center', margin: 0 }}>Submit Your Feedback</h2>

      <input
        placeholder="Name"
        required
        value={formData.name}
        onChange={(e) => setFormData({ ...formData, name: e.target.value })}
        style={{
          padding: '10px',
          fontSize: '14px',
          borderRadius: '5px',
          border: '1px solid #ccc',
          height: '40px',  // Fixed height for consistency
        }}
      />

      <input
        type="email"
        placeholder="Email"
        required
        value={formData.email}
        onChange={(e) => setFormData({ ...formData, email: e.target.value })}
        style={{
          padding: '10px',
          fontSize: '14px',
          borderRadius: '5px',
          border: '1px solid #ccc',
          height: '40px',  // Fixed height for consistency
        }}
      />

      <textarea
        placeholder="Feedback"
        required
        value={formData.text}
        onChange={(e) => setFormData({ ...formData, text: e.target.value })}
        style={{
          padding: '10px',
          fontSize: '14px',
          borderRadius: '5px',
          border: '1px solid #ccc',
          resize: 'vertical',
          minHeight: '80px',  // Ensuring a consistent starting height
        }}
      />

      <select
        value={formData.category}
        onChange={(e) => setFormData({ ...formData, category: e.target.value })}
        style={{
          padding: '10px',
          fontSize: '14px',
          borderRadius: '5px',
          border: '1px solid #ccc',
          height: '40px',  // Fixed height for consistency
        }}
      >
        <option value="suggestion">Suggestion</option>
        <option value="bug">Bug</option>
        <option value="feature">Feature Request</option>
      </select>

      <button
        type="submit"
        style={{
          padding: '15px',
          fontSize: '18px',  // Larger font size for the button
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          marginTop: '10px',
          height: '50px',  // Bigger button height
        }}
      >
        Submit
      </button>
    </form>
  );
};

export default FeedbackForm;
